package main

import (
	"encoding/base64" // 用于 Base64 解码
	"fmt"           // 用于格式化输出
	"log"           // 用于记录错误
	"os/exec"       // 用于执行外部命令
)

func main() {
	// 1. 定义 Base64 编码的命令字符串
	//    "bHMgLWxz" 是 "ls -ls" 的 Base64 编码
	encodedCommand := ""
	fmt.Printf("要解码和执行的 Base64 编码命令: %s\n", encodedCommand)

	// 2. 解码 Base64 字符串
	//    base64.StdEncoding.DecodeString 会将 Base64 字符串解码为字节切片 ([]byte)
	decodedBytes, err := base64.StdEncoding.DecodeString(encodedCommand)
	if err != nil {
		// 如果解码出错，记录错误并退出
		log.Fatalf("Base64 解码失败: %v", err)
	}
	// 将解码后的字节切片转换为字符串
	decodedCommand := string(decodedBytes)
	fmt.Printf("解码后的命令: %s\n", decodedCommand)
	fmt.Println("------ 开始执行解码后的命令 ------")

	// 3. 准备执行解码后的命令
	//    我们使用 "bash" (或 "sh" 在某些系统上更通用) 来执行这个命令。
	//    "-c" 选项告诉 bash 从接下来的字符串参数中读取命令。
	cmd := exec.Command("bash", "-c", decodedCommand)

	// 4. 执行命令并捕获其输出
	//    cmd.CombinedOutput() 会执行命令，并返回标准输出和标准错误合并后的结果。
	output, err := cmd.CombinedOutput()
	if err != nil {
		// 如果命令执行出错（例如，命令不存在或执行失败），
		// 记录错误信息（包括命令的输出，里面可能包含错误详情）并退出。
		log.Fatalf("执行命令 '%s' 失败: %v\n输出:\n%s", decodedCommand, err, string(output))
	}

	// 5. 打印命令执行的输出结果
	fmt.Println("------ 命令执行完毕 ------")
	fmt.Printf("命令输出:\n%s", string(output))
}
